
public class Problema_15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		System.out.println("Calculo del 15% anual durante un lapso de 1961 al 2018");
        int x;
        x=1500;
        while (x<=12825) {
            System.out.print(x);
            System.out.print("\n mas --> 15% = ");
            x = x + 225;
        }
        System.out.println("El ahorro total hasta el 2018 es de: "+x +" pesos mexicanos");
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
